import React , {useState} from 'react'
import './Student.css';
import { useHistory } from "react-router-dom";
import axios from 'axios';

const StudentLogin = ({updateUser}) => {
    
    const history = useHistory();

    const handleCLickSignup = () => {
        history.push("/Student");
    }
    const [ user, setUser] = useState({
        email:"",
        enrollment:"",
        password:""
        
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:9002/StudentLogin", user)
        .then(res => {
            alert(res.data.message)
            setLoginUser(res.data.user)
            history.push("/StudentHome")
        })
    }
    return (
        <>
            <div className="container">
                <div className="form-container">
                {console.log("User", user)}
                    <div className="button-box">
                        <div style={{left:'50%'}} id="btn"></div>
                        <button type="button" style={{outline: 'none'}}  onClick={handleCLickSignup} className="toggle-btn">Sign up</button>
                        <button type="button" style={{outline: 'none'}} className="toggle-btn">Log in</button>
                    </div>
                    <form id="login" className="input-group" >
                    <input type="text" name="email" className="input-field" value={user.email} placeholder="Email ID" onChange={ handleChange } required></input>
                        <input type="text" name="enrollment" className="input-field" value={user.enrollment} placeholder="Enrollement ID"  onChange={ handleChange } required/>
                        <input type="password" name="password" className="input-field" value={user.password} placeholder="Password" onChange={ handleChange } required/>
                        <input type="submit" className="btn-submit" onClick={login} value="Log In" />
                    </form>
                </div>
            </div>
        </>
    )
}

export default StudentLogin
