import React , { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from 'react-bootstrap/Button';
import './Student.css';
import { useHistory } from "react-router-dom";
import axios from "axios";
//import Login from "./login";
const Student = () => {
    // Swift of login and signup module
    const signup = "0px";
    const [currentSignup, setSignup] = useState(signup);
    const login = "400px";
    const [currentLogin, setLogin] = useState(login);
    const btnDiv = "0";
    const [currentBtnDiv, setBtnDiv] = useState(btnDiv);
    const handleCLickLogin = () => {
        let newSignup = "-400px";
        setSignup(newSignup);
        let newLogin = "0px";
        setLogin(newLogin);
        let newBtnDiv = "50%";
        setBtnDiv(newBtnDiv);
    }
    const handleCLickSignup = () => {
        let newSignup = "0px";
        setSignup(newSignup);
        let newLogin = "400px";
        setLogin(newLogin);
        let newBtnDiv = "0";
        setBtnDiv(newBtnDiv);   
    }


    // Modal open close for signup successful
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    //const handleShow = () => setShow(true);
    

    // redirecting to student home on clicking login button
    const history = useHistory();
    const handleLoginButton = () => {
        history.push("/StudentHome");
    }
    const [ user1, setUser1] = useState({
        enrollment1:"",
        email1:"",
        password1:""
    })

    const [ user, setUser] = useState({
        email:"",
        enrollment:"",
        password:"",
        reEnterPassword:""
    })
    
    const handleChange1 = e => {
        const { name1, value1 } = e.target
        setUser1({
            ...user1,
            [name1]: value1
        })
    }
    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }
    
    const Student = () => {
        const { email , enrollment , reEnterPassword , password } = user
        if( email && enrollment && password && (password === reEnterPassword)){
            axios.post("http://localhost:9002/Student", user)
            .then( res => {
                alert(res.data.message)
                history.push("/Student")
            })
        } else {
            alert("invlid input")
        }
    
        axios.post("http://localhost:9002/Student", user)
    }

    return (
        <>
            <div className="container">
                 
                <div className="form-container">
                    {console.log("User", user)}
                    <div className="button-box">
                        <div style={{left: currentBtnDiv}} id="btn"></div>
                            <button type="button" style={{outline: 'none'}}  onClick={handleCLickSignup} className="toggle-btn">Sign up</button>
                            
                    </div>
                    <form style={{left: currentSignup}}id="signup" className="input-group">
                        <input type="text" name="email" className="input-field" value={user.email} placeholder="Email ID" onChange={ handleChange } required></input>
                        <input type="text" name="enrollment" className="input-field" value={user.enrollment}  placeholder="Enrollement ID" onChange={handleChange} required/>
                        <input type="password" name="password" className="input-field" value={user.password}  placeholder="Password" onChange={handleChange} required/>
                        <input type="reenterpassword" name="reEnterPassword" className="input-field" value={user.reEnterPassword} placeholder="Re-enter Password" onChange={ handleChange }></input>
                        <button type="button" onClick={Student} className="btn-submit" >Sign up</button>
                    </form>
                    
                    <form style={{left: currentLogin}} id="login" className="input-group">
                        
                        <input type="text" name="enrollment1" className="input-field" value1={user1.enrollment1}  placeholder="Enrollement ID" onChange={handleChange1}  required/>
                        <input type="text" name="email1" className="input-field" value1={user1.email1}  placeholder="Email ID" onChange={handleChange1}  required></input>
                        <input type="password" name="password1"  className="input-field" value1={user1.password1} placeholder="Password" onChange={handleChange1}  required/>
                        <button type="button" onClick={handleLoginButton} className="btn-submit" >Log In</button>
                    </form>
                    <div>or</div>
                    <div className="button" onClick={() => history.push("/login")}>Login</div>
                </div>
            </div> 
            <Modal show={show} onHide={handleClose}>
                <Modal.Header>
                    <Modal.Title>Hello Student !!! </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Welcome to Depstar Student Grievance Redressal Portal📫 
                    <br/> Woohoo, you're signup is successful 👍
                    <br/> <br/> Please login with the credentials to proceed further.</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Okay
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}
export default Student
