import React,{useState,useEffect} from 'react';
import './Reply.css';
import { useParams } from 'react-router';
import Table from 'react-bootstrap/Table';
const Reply = () => {

    const params= useParams()
    // console.log(params);
     const [complaint , setComplaint]= useState([]);
     function fetchdata () {
         fetch('http://localhost:8080/api/auth/complaint',{method: 'POST' ,headers: {
             'Content-Type': 'application/json'
             // 'Content-Type': 'application/x-www-form-urlencoded',
           }, body: JSON.stringify({id:params.id})  }).then((data)=>{
          console.log(data);   
         return data.json()
         })
         .then((data)=>{
             console.log(data)
                 setComplaint(data);
         })
     }
     useEffect(()=>{
             fetchdata();
     },[]) 
 

    return (
        <>
            <div className="introReply">
                <p className="firstLineReply">
                    Redressal to your Grievance from Faculty
                </p>
                <Table striped bordered>
                    <thead style={{backgroundColor:'#39b9d2'}}>
                        <tr>
                            <th>Grievance ID</th>
                            <th>Grievance Subject</th>
                            <th>Grievance Redressal</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{}</td>
                            <td>{}</td>
                            <td>-</td>
                        </tr>
                        <tr>
                            <td>-</td>
                            <td>-</td>
                            <td>-</td>
                        </tr>
                    </tbody>
                </Table>
            </div>
        </>
    )
}

export default Reply
