import React,{useState,useEffect} from 'react';
import './Reply.css';
import { useHistory } from 'react-router';
//import { useParams } from 'react-router';
import Table from 'react-bootstrap/Table';
const Reply = () => {
    const history = useHistory();
    const [complain , setTrial]= useState([]);
    function fetchdata () {
        fetch('http://localhost:8080/api/auth/').then((data)=>{
            return data.json();
        })
        .then((data)=>{
            console.log(data)
                setTrial(data);
        })
    }
    useEffect(()=>{
            fetchdata();
    },[]) 

    return (
        <>
            <div className="introReply">
                <p className="firstLineReply">
                    Redressal to your Grievance from Faculty
                </p>
                <Table striped bordered>

    
                    <thead style={{backgroundColor:'#39b9d2'}}>
                        <tr>
                            <th>Grievance ID</th>
                            <th>Grievance Subject</th>
                            <th>Grievance Redressal</th>
                            <th>Student id</th>
                        </tr>
                    </thead>
                    <tbody>
                    {complain?.map((cmp)=>{
                            return <tr onClick={()=>{
                                //history.push('/complaint/'+cmp?._id)
                            }}>
                            <td>{cmp?._id}</td>
                            <td>{cmp?.grievancesub}</td>
                            <td>{cmp?.redressal}</td>
                            <td>{cmp?.studentid}</td>
                        </tr>
                    })}
                    </tbody>
                </Table>
            </div>
        </>
    )
}

export default Reply
