import React , {useState} from 'react'
import './Student.css';
import { useHistory } from "react-router-dom";

const StudentLogin = ({login})=> {
    
    const history = useHistory();

    const handleCLickSignup = () => {
        history.push("/Student");
    }
    const [loginData, SetLoginData] = useState({
        email: '',
        password: '',
      });

      const { email, password } = loginData;
      const onChange = (e) =>
      SetLoginData({ ...loginData, [e.target.value]: e.target.value });
  

      const onSubmit = (e) => {
        e.preventDefault();
        login(email, password);
      };
    
    return (
        <>
            <div className="container">
                <div className="form-container">
                
                    <div className="button-box">
                        <div style={{left:'50%'}} id="btn"></div>
                        <button type="button" style={{outline: 'none'}}  onClick={handleCLickSignup} className="toggle-btn">Sign up</button>
                        <button type="button" style={{outline: 'none'}} className="toggle-btn">Log in</button>
                    </div>
                    <form id="login" className="input-group" >
                    <input type="text" name="email" className="input-field" value={email} placeholder="Email ID" onChange={(e) => onChange(e)} required></input>
                        <input type="password" name="password" className="input-field" value={password} placeholder="Password" onChange={(e) => setPassword(e.target.value)} required/>
                        <input type="submit" className="btn-submit" onClick={onSubmit} value="Log In" />
                    </form>
                </div>
            </div>
        </>
    )
}

export default StudentLogin
