import React, { useState } from 'react'
import { BrowserRouter, Route } from "react-router-dom";
import Navbar from './components/Navbar';
import Home from './components/Home';
import Student from './components/Student';
import Contact from './components/Contact';
import About from './components/About';
import StudentHome from './components/StudentHome';
import Register from './components/Register';
import Status from './components/Status';
import ShowId from './components/ShowId';
import Reply from './components/Reply';
import Faq from './components/Faq';
import FacultyHome from './components/FacultyHome';
import Redressal from './components/Redressal';
import StudentLogin from './components/StudentLogin';
import { BrowserRouter as Router , Switch } from 'react-router-dom';
import { useEffect} from 'react';

const App = () => {

  const [ user , setLoginUser ] = useState({})

  useEffect(() => {
    setLoginUser(JSON.parse(localStorage.getItem("MyUser")))
  }, [])

  const updateUser = (user) => {
    localStorage.setItem("MyUser", JSON.stringify(user))
    setLoginUser(user)
  }

  return (
    <>
    <BrowserRouter>
    <Navbar/>
    <Route exact path="/">
         {
          user && user._id ? <StudentHome updateUser={updateUser}/> : <StudentLogin updateUser = {updateUser}/>
         }
        <Home/>
        </Route>
      <Route path="/StudentLogin">
      <StudentLogin updateUser={updateUser}/>
    </Route>
    <Route path="/Student">
      <Student/>
    </Route>  
    <Route path="/Faq">
      <Faq/>
    </Route>
    <Route path="/About">
      <About/>
    </Route>
    <Route path="/Contact">
      <Contact/>
    </Route>
    <Route path="/StudentHome">
    </Route>
    <Route path="/Register">
      <Register/>
    </Route>
    <Route path="/Status">
      <Status/>
    </Route>
    <Route path="/ShowId">
      <ShowId/>
    </Route>
    <Route path="/Reply">
      <Reply/>
    </Route>
    <Route path="/FacultyHome">
      <FacultyHome/>
    </Route>
    <Route path="/Redressal">
      <Redressal/>
    </Route>
    </BrowserRouter>
    </> 
  )
}


export default App
