import React from "react";
import Modal from "react-bootstrap/Modal";
import Button from 'react-bootstrap/Button';
import './Student.css';
import { useState } from 'react';
import { useHistory } from "react-router-dom";


const Student = () => {
    // Swift of login and signup module
    const signup = "0px";
    const [currentSignup, setSignup] = useState(signup);
    const login = "400px";
    const [currentLogin, setLogin] = useState(login);
    const btnDiv = "0";
    const [currentBtnDiv, setBtnDiv] = useState(btnDiv);
    const handleCLickLogin = () => {
        let newSignup = "-400px";
        setSignup(newSignup);
        let newLogin = "0px";
        setLogin(newLogin);
        let newBtnDiv = "50%";
        setBtnDiv(newBtnDiv);
    }
    const handleCLickSignup = () => {
        let newSignup = "0px";
        setSignup(newSignup);
        let newLogin = "400px";
        setLogin(newLogin);
        let newBtnDiv = "0";
        setBtnDiv(newBtnDiv);   
    }


    // Modal open close for signup successful
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    // redirecting to student home on clicking login button
    const history = useHistory();
    const handleLoginButton = () => {
        history.push("/StudentHome");
    }
    return (
        <>
            <div className="container">
                <div className="form-container">
                    <div className="button-box">
                        <div style={{left: currentBtnDiv}} id="btn"></div>
                            <button type="button" style={{outline: 'none'}}  onClick={handleCLickSignup} className="toggle-btn">Sign up</button>
                            <button type="button" style={{outline: 'none'}} className="toggle-btn" onClick={handleCLickLogin}>Log in</button>
                        
                    </div>
                    <form style={{left: currentSignup}} id="signup" className="input-group">
                        <input type="email" className="input-field" placeholder="Email ID" required/>
                        <input type="text" className="input-field" placeholder="Enrollement ID" required/>
                        <input type="password" className="input-field" placeholder="Password" required/>
                        <button type="button" onClick={register} className="btn-submit" >Sign Up</button>
                    </form>
                    <form style={{left: currentLogin}} id="login" className="input-group">
                        <input type="text" className="input-field" placeholder="Enrollement ID" required/>
                        <input type="password" className="input-field" placeholder="Password" required/>
                        <button type="button" onClick={handleLoginButton} className="btn-submit" >Log In</button>
                    </form>
                </div>
            </div>    
            <Modal show={show} onHide={handleClose}>
                <Modal.Header>
                    <Modal.Title>Hello 19DCS001 !!! </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Welcome to Depstar Student Grievance Redressal Portal📫 
                    <br/> Woohoo, you're signup is successful 👍
                    <br/> <br/> Please login with the credentials to proceed further.</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Okay
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default Student
